
public class Example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=5, b=3;
System.out.println(a++ +b);
System.out.println(a);
System.out.println(++a);
System.out.println(a);
System.out.println(--a);
	System.out.println(a-- +b);
System.out.println(a);
System.out.println(--b);

	
	}
	
}

